import React, { Fragment } from "react";
import Button from "../Form/Button";
import "./Error.css";
import ReactDOM from 'react-dom'

function Error(props) {
  const Backdrop = (props) => {
    return <div className="back" onClick={props.onChange} />;
  };
  const OverLay = props => {
    <div className="error">
    <header>
      <h2>{props.title}</h2>
    </header>
    <div>
      <p>{props.message}</p>
    </div>
    <footer>
      <Button onClick={props.onChange}>Okay</Button>
    </footer>
  </div>
  }


  return (
    <Fragment>
    {ReactDOM.createPortal(<Backdrop onClick={props.onChange} />,document.getElementById('backdrop-root'))}
    {ReactDOM.createPortal()}
    </Fragment>      
  )
}

export default Error;
