    username: "123",
    age: "123",
  });

  const retrieveData = (prev) =>{
    const allUsers = [prev,...users]
    setNewUsers
  }

  return (
    <div className="app">
      <Form onHandleSubmit={retrieveData} />
      <NewUser username={users.username} age={users.age}/>
    </div>
  );
};

export default App;
