import React, { useState, useRef } from "react";
import Error from "../Error/Error";
import Form from "../Form/Form";
import "./NewUser.css";

function NewUser(props) {
  const [users, setNewUsers] = useState([]);

  console.log(users);

  const retrieveData = (prev) => {
    const allUsers = [prev, ...users];

    setNewUsers(allUsers);
  };
  const delteItem = (id) => {
    const removeUser = allUsers.filter((user=>{
      return user.id
    }));
  };
  return (
    <div>
      <Form onHandleSubmit={retrieveData} />
      <div className="all">
        {users.map((user) => (
          <div key={user.id} className="userList">
            <div>
              <div id="user">
                {user.username} {"(" + user.age + " years old" + ")"}
              </div>
              <div id="underline"></div>
            </div>
            <i onClick={() => delteItem(props.id)}>
              <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18">
                <path
                  fill="#494C6B"
                  fill-rule="evenodd"
                  d="M16.97 0l.708.707L9.546 8.84l8.132 8.132-.707.707-8.132-8.132-8.132 8.132L0 16.97l8.132-8.132L0 .707.707 0 8.84 8.132 16.971 0z"
                />
              </svg>
            </i>
          </div>
        ))}
      </div>
    </div>
  );
}

export default NewUser;
