import React, { ChangeEvent, useState } from "react";
import Error from "../Error/Error";
import Button from "./Button";
import "./Form.css";

function Form(props) {
  const [username, setUserName] = useState("");
  const [age, setUserAge] = useState("");
  const [error,setError] = useState()
  const userNameInput = (usernameInput) => {
    setUserName(usernameInput.target.value);
  };
  const userAgeInput = (userageInput) => {
    setUserAge(userageInput.target.value);
  };
  const returnNewUser = (event) => {
    event.preventDefault();
    if(username.trim().length ===0 || age.trim().length===0){
      setError({
        title:"invalid Input",
        message : "Please enter a valid age and username"
      })
      return ;
    }
    if(age<1){
      setError({
        title:"invalid age",
        message : "please enter a valid age number"
      })
      return ;
    }

    const newUser = {
      username: username,
      age: age,
    };
    setUserName("");
    setUserAge("");
    props.onHandleSubmit(newUser);
  };

  return (
    <div className="wErr">
      {error && <Error  title={error.title} message={error.message}/>}
      <form className="form" action="submit" onSubmit={returnNewUser}>
        <div className="input">
          <input
            onChange={userNameInput}
            type="text"
            id="username"
            name="username"
            value={username}
            className={`${username ? "has-value" : ""}`}
          />
          <label id="username" htmlFor="username">
            Username
          </label>
        </div>
        <div className="input">
          <input
            onChange={userAgeInput}
            type="number"
            id="age"
            name="age"
            value={age}
            className={`${age ? "has-value" : ""}`}
          />
          <label id="age" htmlFor="age">
            Age (Years){" "}
          </label>
        </div>
        <Button type="submit">Add User</Button>
      </form>
    </div>
  );
}

export default Form;
