import React, {ChangeEvent, useState } from "react";
import "./Form.css";

function Form(props) {
  const [username, setUserName] = useState('');
  const [age, setUserAge] = useState('');

  const userNameInput = (usernameInput) => {
    setUserName(usernameInput.target.value);
    console.log(username);
  };
  const userAgeInput = (userageInput) => {
    setUserAge(userageInput.target.value);
    console.log(age);
  };
  const returnNewUser = (event) => {
    event.preventDefault();

    const newUser = {
      username: username,
      age: age,
    };
    setUserName('')
    setUserAge('')
    props.onHandleSubmit(newUser)


    // if(newUser.username===0 || newUser.age.length===0){
    // }
    // setUserAge('')
    // setUserName('')
    // console.log(newUser)
  };


  return (
    <div>
      <form className="form" action="submit" onSubmit={returnNewUser}>
        <div className="input">

          <input
            onChange={userNameInput}
            type="text"
            id="username"
            name="username"
            placeholder="Enter a Username"
            value={username}
            className={`${username ? "hasa-value" : ""}`}
          />
                  <label htmlFor="username">Username</label>

        </div>
        <div className="input">
          <label htmlFor="age">Age (Years) </label>
          <input
            onChange={userAgeInput}
            type="number"
            id="age"
            name="age"
            placeholder="Enter age in years"
            value={age}
          />
        </div>
        <button>Add User</button>
      </form>
    </div>
  );
}

export default Form;
